    pdf.setTextColor(...white);
    pdf.text('QtÃ©', colQty + 3, y + 6);
    pdf.text('RÃ©fÃ©rence', colRef, y + 6);
    pdf.text('DÃ©signation', colDesc, y + 6);
    pdf.text('Prix Unit.', colUnit, y + 6, { align: 'right' });
    pdf.text('Total HT', colTotal, y + 6, { align: 'right' });
