                  const devs = qd.devices || [];
                  const devTypes = [...new Set(devs.map(d => d.deviceType).filter(Boolean))];
                  if (devTypes.length === 0) devTypes.push('particle_counter');
                  const hasNett = devs.some(d => d.needsNettoyage && (d.nettoyagePrice || 0) > 0);
                  const sDate = qd.contractDates?.start_date ? new Date(qd.contractDates.start_date).toLocaleDateString('fr-FR', { day: 'numeric', month: 'long', year: 'numeric' }) : 'â€”';
                  const eDate = qd.contractDates?.end_date ? new Date(qd.contractDates.end_date).toLocaleDateString('fr-FR', { day: 'numeric', month: 'long', year: 'numeric' }) : 'â€”';
                  const shipT = qd.shippingTotal || 0;
