                  const totTokens = qd.totalTokens || devs.reduce((s, d) => s + (d.tokens_total || d.calibrationQty || 1), 0);
                  const cNum = contract.contract_number || qd.contractNumber || '';
                  const cDate = (qd.createdAt || contract.quote_sent_at) ? (() => { try { return new Date(qd.createdAt || contract.quote_sent_at).toLocaleDateString('fr-FR', { day: 'numeric', month: 'long', year: 'numeric' }); } catch { return 'â€”'; } })() : new Date().toLocaleDateString('fr-FR', { day: 'numeric', month: 'long', year: 'numeric' });
                  const cConditions = [
                    'Devis valable 30 jours Ã  compter de la date d\'Ã©mission.',
                    `PÃ©riode du contrat : du ${sDate} au ${eDate}.`,
                    `${totTokens} Ã©talonnage(s) inclus pendant la pÃ©riode contractuelle.`,
                    'Ã‰talonnages supplÃ©mentaires facturÃ©s au tarif standard.',
                    "Cette offre n'inclut pas la rÃ©paration ou l'Ã©change de piÃ¨ces non consommables.",
                    'Un devis complÃ©mentaire sera Ã©tabli si des piÃ¨ces sont trouvÃ©es dÃ©fectueuses.',
                    'Les mesures stockÃ©es dans les appareils seront Ã©ventuellement perdues.',
                    'Paiement Ã  30 jours date de facture.'
                  ];
                  const bAddr = qd.billingAddress || null;
                  const cName = bAddr?.company_name || company.name || contract.client_name || 'Client';
                  const totalRows = devs.length + devs.filter(d => d.needsNettoyage && (d.nettoyagePrice || 0) > 0).length + (shipT > 0 ? 1 : 0);
                  const multiPage = totalRows > 4 || devTypes.length > 1;
