              <div>
                <p className="text-red-800 font-bold">{lang === 'en' ? `Invoice overdue â€” ${daysPastDue} day${daysPastDue > 1 ? 's' : ''}` : `Facture en retard â€” ${daysPastDue} jour${daysPastDue > 1 ? 's' : ''}`}</p>
                <p className="text-red-600 text-sm">{lang === 'en' ? 'Due:' : 'Ã‰chÃ©ance:'} {frenchDate(inv.due_date)} â€¢ {lang === 'en' ? 'Remaining:' : 'Reste dÃ»:'} {remaining.toFixed(2)} â‚¬</p>
              </div>
