  particle_counter: { icon: 'ðŸ”¬', label: { en: 'Airborne Particle Counter', fr: 'Compteur de Particules AÃ©roportÃ©es' } },
  bio_collector: { icon: 'ðŸ§«', label: { en: 'Bio Collector', fr: 'Bio Collecteur' } },
  liquid_counter: { icon: 'ðŸ’§', label: { en: 'Liquid Particle Counter', fr: 'Compteur Particules Liquide' } },
  temp_humidity: { icon: 'ðŸŒ¡ï¸', label: { en: 'Temp/Humidity Sensor', fr: 'Capteur Temp/HumiditÃ©' } },
